import axios from 'axios';
const API_HOST = "http://192.168.0.106:8000/"
const FormData = require('form-data');

export async function getViewGET(endpointUrl=null) {
    try {
        const headers = {
            'X-Inertia': true,
            'Content-Type': 'application/json'
        }
        const url = endpointUrl  ? `${API_HOST}${endpointUrl}` : `${API_HOST}`;
        const response = await axios({ method: "get", url: url, headers: headers });
        console.log(response.data);
        return response;
    } catch (error) {
        throw error;
    }
}

export async function getViewPOST(endpointUrl=null, data={}, csrftoken="") {
    try {
        const headers = { 
            'X-Inertia': true,
            'Content-Type': 'application/json',
            'HTTP_X_XSRF_TOKEN': csrftoken,
        }
        console.log(headers)
        const url = endpointUrl  ? `${API_HOST}${endpointUrl}` : `${API_HOST}`;
        const response = await axios({ method: "post", url: url, headers: headers, data: data});
        console.log(response.data);
        return response;
    } catch (error) {
        throw error;
    }
}
