import axios from 'axios';
const API_HOST = "http://192.168.0.104:8000/"

export async function getView(endpointUrl=null) {
    try {
        const headers = { 'X-Inertia': true }
        const url = endpointUrl  ? `${API_HOST}${endpointUrl}` : `${API_HOST}`;
        const response = await axios.get(url, { headers: headers });
        return response;
    } catch (error) {
        throw error;
    }
}
