import React, { useEffect, useState } from 'react';
import { View, Text } from 'react-native'
import { getView } from '../api/getView';
import useInertia from '../hooks/useInertia';


export default function Home() {
    const [dataProps, setDataProps] = useState(null);
    const { iProps, setValues } = useInertia();

    useEffect(() => {
        (async() =>{
            setDataProps(iProps)
            const response = await getView();
            setValues(response.data);
            setDataProps(response.data.props)
            console.log(dataProps)
        })()
    },[])

    return (
        !dataProps ? 
        (<Text>User no Logged</Text>)
        :
        (<View>
            <Text>Home</Text>
            <Text>Props: {dataProps ? dataProps.auth.user.username : ""}</Text>
        </View>)
    )
}
